# == NBRES DE PSEUDOS CONTENUS DANS L'ARRAY AU TOTAL ==

def how_many_journalists(journalists)
    puts "Il y a : #{journalists.length} journalistes dans cette array."
end

# == NBRES MOYEN DE CARACTERES DANS LES PSEUDOS DES JOURNALISTS ==

def journalists_name_avg(journalists)
    journalists_name_avg = 0

    journalists.each do |journalist|
        journalists_name_avg += journalist.length
    end

    journalists_name_avg = journalists_name_avg / journalists.length
    puts "En moyenne les pseudos des journalistes contiennent #{journalists_name_avg} caractères."
end

# == NBRE DE JOURNALISTES CONTENANT UN NBRE DANS LEURS PSEUDO ==

def journalists_names_with_numbers(journalists)
    journalists_names_with_numbers = 0

    journalists.each do |journalist|
        if  journalist.match?(/[0-9]/)    
            journalists_names_with_numbers += 1
        else
        end
    end

    puts "Il y a #{journalists_names_with_numbers} pseudos de journalistes contenant des nombres."
end 

# == NBRE DE JOURNALISTES CONTENANT UN "AUDE" DANS LEURS PSEUDOS PEU IMPORTE LA CASE ==

def journalists_names_with_aude(journalists)
    journalists_names_with_aude = 0

    journalists.each do |journalist|
        if  journalist.downcase.match?(/aude/)    
            journalists_names_with_aude += 1
        else
        end
    end

    puts "Il y a #{journalists_names_with_aude} pseudos de journalistes contenant la chaîne de caractère 'aude'."
end

# == NBRE DE JOURNALISTES DONT LE PSEUDO COMMENCE PAR UNE MAJUSCULE APRES LE @ ==

def journalists_names_with_upper_at_start(journalists)
    journalists_names_with_upper_at_start = 0

    journalists.each do |journalist|
        if  journalist[1].match?(/[[:upper:]]/)  # méthode alternative -> journalist.match?(/^.{1}[[:upper:]]/)
            journalists_names_with_upper_at_start += 1
        else
        end
    end

    puts "Il y a #{journalists_names_with_upper_at_start} pseudos de journalistes commençant par une majuscule après le @."
end

# == NBRE DE JOURNALISTES AYANT UNE MAJUSCULE DANS LEURS PSEUDO ==

def journalists_names_with_upper(journalists)
    journalists_names_with_upper = 0

    journalists.each do |journalist|
        if  journalist.match?(/[[:upper:]]/)    
            journalists_names_with_upper += 1
        else
        end
    end

    puts "Il y a #{journalists_names_with_upper} pseudos de journalistes contenant une majuscule."
end

# == NBRE DE _ AU TOTAL ==

def journalists_underscore(journalists)
    journalists_underscore = 0

    under = journalists.join    
    journalists_underscore = under.count "_"

=begin
    journalists.each do |journalist|
        journalist_split = journalist.split(//)
        journalist_split.each do |journalistsplit|
            if  journalistsplit.match?(/_/)    
            journalists_underscore += 1     
            else
            end
        end
    end
=end

    puts "Il y a #{journalists_underscore} underscores dans au total dans les pseudos des journalistes."
end

# == TRIER L'ARRAY ==

def journalists_sort(journalists)
    journalists_sort = [] 

    journalists.each do |journalist|
        journalists_sort << journalist.downcase
        end

    puts journalists_sort.sort.inspect   
end

# == LES 50 PSEUDOS LES PLUS COURTS ==

def journalists_short(journalists)

    puts journalists.sort_by(&:length).take(50).inspect
  
end

# == TROUVER L'INDEX DE @epenser ==

def journalists_index(journalists)
    puts "L'index de @epenser est : #{journalists.index ("@epenser")}"
end
  
how_many_journalists(journalists)
journalists_name_avg(journalists)
journalists_names_with_numbers(journalists)
journalists_names_with_aude(journalists)
journalists_names_with_upper_at_start(journalists)
journalists_names_with_upper(journalists)
journalists_underscore(journalists)
journalists_sort(journalists)
journalists_index(journalists)
journalists_short(journalists)
